mapper.select:
	SELECT *
       FROM (
           SELECT
               ROW_NUMBER() OVER(ORDER BY msg_seq DESC) AS NUM,
               A.*
           FROM
               BOARD A
           WHERE board_depth like #{board_depth}
           AND board_id like #{board_id}
       )B

mapper.update:
UPDATE BOARD SET MSG_CONTENT=#{msg_content}
WHERE BOARD_DEPTH LIKE #{board_depth}
AND BOARD_ID LIKE #{board_id}
AND MSG_SEQ LIKE #{msg_seq}

mapper.delete:
DELETE FROM BOARD 
WHERE BOARD_DEPTH LIKE #{board_depth}
AND BOARD_ID LIKE #{board_id}
AND MSG_SEQ LIKE #{msg_seq}

DELETE FROM BOARD 
WHERE BOARD_ID LIKE #{board_id}
AND MSG_SEQ LIKE #{msg_seq}
